import React from 'react';
import axios from 'axios';

import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead'
import TableBody from '@material-ui/core/TableBody'
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';

import './App.css';

class Album extends React.Component {
    render() {
        const {id, name, artist} = this.props;
        const imgsrc = "http://moonmusic.duckdns.org:3000/images/" + id + ".jpg";

        return (
            <TableRow>
                <TableCell>{id}</TableCell>
                <TableCell><img src={imgsrc} alt={name} /></TableCell>
                <TableCell>{name}</TableCell>
                <TableCell>{artist}</TableCell>
            </TableRow>
        );
    }
}

class App extends React.Component {

    state = {
        albums: []
    }

    componentDidMount() {
        this.getAlbums();
    }

    getAlbums = async () => {
        var url = "http://moonmusic.duckdns.org:8081/api/albums";
        const albums = await axios.get(url);
        this.setState({albums:albums.data});
    } 

    render() {
        const {albums} = this.state;

        return (
            <div>
                <center>
                    <h1>
                        대중 음악의 이해
                    </h1>
                    <h2>
                        정기 음평회 UPLOAD WEBPAGE
                    </h2>
                </center>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>음평회 순서</TableCell>
                            <TableCell>Album Cover</TableCell>
                            <TableCell>Album Name</TableCell>
                            <TableCell>Artist</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {albums.map(album => (
                            <Album 
                                key = {album.id}
                                id = {album.id}
                                name = {album.name}
                                artist = {album.artist}
                            />
                        ))}
                    </TableBody>
                </Table>
            </div>
        );
    }
}

export default App;
